import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      pelanggan: []
    };
  }

  componentDidMount() {
    axios.get('/pelanggan')
      .then(res => {
        this.setState({ pelanggan: res.data });
        console.log(this.state.pelanggan);
      });
  }
  delete(id){
    console.log(id);
    axios.delete('/pelanggan/'+id)
      .then((result) => {
        this.props.history.push("/")
      });
  }

  render() {
    return (
      <div>
        <div>
          <div>
            <h3>
              DATA PELANGGAN
            </h3>
          </div>
          <div>
            <h4><Link to="/create"> Tambah Pelanggan</Link></h4>
            <table>
              <thead>
                <tr>
                  <th>nama</th>
                  <th>alamat</th>
                  <th>no telp</th>
                  <th>umur</th>
                  <th>aksi</th>
                </tr>
              </thead>
              <tbody>
                {this.state.pelanggan.map(datapelanggan =>
                  <tr key={datapelanggan._id}>
                    <td><Link to={`/show/${datapelanggan._id}`}>{datapelanggan.nama}</Link></td>
                    <td>{datapelanggan.alamat}</td>
                    <td>{datapelanggan.notelp}</td>
                    <td>{datapelanggan.umur}</td>
                    <td>
                      <Link to={`/edit/${datapelanggan._id}`}>edit</Link>
                      &nbsp;&nbsp;
                      <button onClick={this.delete.bind(this, datapelanggan._id)} class="btn btn-danger">Delete</button>
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
