import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

class Edit extends Component {

  constructor(props) {
    super(props);
    this.state = {
      pelanggan: {}
    };
  }

  componentDidMount() {
    axios.get('/pelanggan/'+this.props.match.params.id)
      .then(res => {
        this.setState({ pelanggan: res.data });
        console.log(this.state.pelanggan);
      });
  }

  onChange = (e) => {
    const state = this.state.pelanggan
    state[e.target.name] = e.target.value;
    this.setState({pelanggan:state});
  }

  onSubmit = (e) => {
    e.preventDefault();
    const { nama,alamat,notelp,umur } = this.state.pelanggan;
    axios.put('/pelanggan/'+this.props.match.params.id, { nama,alamat,notelp,umur})
      .then((result) => {
        // this.props.history.push("/show/"+this.props.match.params.id)
        this.props.history.push("/")
      });
  }

  render() {
    return (
      <div>
        <div>
          <div>
            <h3>
              EDIT pelanggan
            </h3>
          </div>
          <div>
            <h4><Link to={`/show/${this.state.pelanggan._id}`}><span class="glyphicon glyphicon-eye-open" aria-hidden="true"></span> pelanggan List</Link></h4>
            <form onSubmit={this.onSubmit}>
              <div class="form-group">
                <label for="nama">nama:</label>
                <input type="text"  name="nama" value={this.state.pelanggan.nama} onChange={this.onChange} placeholder="nama" />
              </div>

              <div class="form-group">
                <label for="alamat">alamat:</label>
                <input type="text"  name="alamat" value={this.state.pelanggan.alamat} onChange={this.onChange} placeholder="alamat" />
              </div>
              <div class="form-group">
                <label for="umur">umur:</label>
                <input type="number"  name="umur" value={this.state.pelanggan.umur} onChange={this.onChange} placeholder="umur" />
              </div>
              <div class="form-group">
                <label for="notelp">Publisher:</label>
                <input type="text"  name="notelp" value={this.state.pelanggan.notelp} onChange={this.onChange} placeholder="notelp" />
              </div>
              <button type="submit" class="btn btn-default">Submit</button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default Edit;
