import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

class Create extends Component {

  constructor() {
    super();
    this.state = {
      nama: '',
      alamat: '',
      umur: '',
      notelp: '',
    };
  }
  onChange = (e) => {
    const state = this.state
    state[e.target.name] = e.target.value;
    this.setState(state);
  }

  onSubmit = (e) => {
    e.preventDefault();
    const { nama,alamat,notelp,umur } = this.state;

    axios.post('/pelanggan', {nama,alamat,notelp,umur })
      .then((result) => {
        this.props.history.push("/")
      });
  }

  render() {
    const { nama,alamat,notelp,umur } = this.state;
    return (
      <div class="container">
        <div class="panel panel-default">
          <div class="panel-heading">
            <h3 class="panel-title">
              tambah pelanggan
            </h3>
          </div>
          <div class="panel-body">
            <h4><Link to="/"><span class="glyphicon glyphicon-th-list" aria-hidden="true"></span>data pelanggan</Link></h4>
            <form onSubmit={this.onSubmit}>
              <div class="form-group">
                <label for="nama">nama:</label>
                <input type="text" name="nama" value={nama} onChange={this.onChange} placeholder="nama" />
              </div>
              <div class="form-group">
                <label for="alamat">alamat:</label>
                <textArea name="alamat" onChange={this.onChange} placeholder="alamat" cols="80" rows="3">{alamat}</textArea>
              </div>
              <div class="form-group">
                <label for="umur">umur:</label>
                <input type="number"name="umur" value={umur} onChange={this.onChange} placeholder="umur" />
              </div>
              <div class="form-group">
                <label for="notelp">no telp:</label>
                <input type="text"name="notelp" value={notelp} onChange={this.onChange} placeholder="no telp" />
              </div>
              <button type="submit" class="btn btn-default">Submit</button>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default Create;
