import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      pelanggan: []
    };
  }

  componentDidMount() {
    axios.get('/pelanggan')
      .then(res => {
        this.setState({ pelanggan: res.data });
        console.log(this.state.pelanggan);
      });
  }

  render() {
    return (
      <div>
        <div>
          <div>
            <h3>
              BOOK CATALOG
            </h3>
          </div>
          <div>
            <h4><Link to="/create"> Add Book</Link></h4>
            <table>
              <thead>
                <tr>
                  <th>ISBN</th>
                  <th>Title</th>
                  <th>Author</th>
                </tr>
              </thead>
              <tbody>
                {this.state.pelanggan.map(datapelanggan =>
                  <tr key={datapelanggan._id}>
                    <td><Link to={`/show/${datapelanggan._id}`}>{datapelanggan.umur}</Link></td>
                    <td>{datapelanggan.nama}</td>
                    <td>{datapelanggan.alamat}</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
