import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import { Link } from 'react-router-dom';
import axios from 'axios';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      pelanggan: []
    };
  }

  componentDidMount() {
    axios.get('/pelanggan')
      .then(res => {
        this.setState({ books: res.data });
        console.log(this.state.pelanggan);
      });
  }

  render() {
    return (
      <div class="container">
        <div class="panel panel-default">
          <div class="panel-heading">
            <h3 class="panel-title">
              BOOK CATALOG
            </h3>
          </div>
          <div class="panel-body">
            <h4><Link to="/create"><span class="glyphicon glyphicon-plus-sign" aria-hidden="true"></span> Add Book</Link></h4>
            <table class="table table-stripe">
              <thead>
                <tr>
                  <th>ISBN</th>
                  <th>Title</th>
                  <th>Author</th>
                </tr>
              </thead>
              <tbody>
                {this.state.pelanggan.map(datapelanggan =>
                  <tr>
                    <td><Link to={`/show/${datapelanggan._id}`}>{datapelanggan.umur}</Link></td>
                    <td>{datapelanggan.nama}</td>
                    <td>{datapelanggan.alamat}</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
