import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

class Show extends Component {

  constructor(props) {
    super(props);
    this.state = {
      pelanggan: {}
    };
  }

  componentDidMount() {
    axios.get('/pelanggan/'+this.props.match.params.id)
      .then(res => {
        this.setState({ pelanggan: res.data });
        console.log(this.state.pelanggan);
      });
  }

  delete(id){
    console.log(id);
    axios.delete('/pelanggan/'+id)
      .then((result) => {
        this.props.history.push("/")
      });
  }

  render() {
    return (
      <div>
        <div>
          <div>
            <h3>
              {this.state.pelanggan.title}
            </h3>
          </div>
          <div>
            <h4><Link to="/">pelanggan List</Link></h4>
            <dl>
              <dt>Nama:</dt>
              <dd>{this.state.pelanggan.nama}</dd>
              <dt>Alamat:</dt>
              <dd>{this.state.pelanggan.alamat}</dd>
              <dt>Umur:</dt>
              <dd>{this.state.pelanggan.umur}</dd>
              <dt>No telp:</dt>
              <dd>{this.state.pelanggan.notelp}</dd>
            </dl>
            <Link to={`/edit/${this.state.pelanggan._id}`} class="btn btn-success">Edit</Link>&nbsp;
            <button onClick={this.delete.bind(this, this.state.pelanggan._id)} class="btn btn-danger">Delete</button>
          </div>
        </div>
      </div>
    );
  }
}

export default Show;
